import { TestBed, inject, async } from '@angular/core/testing';

import { RemoteUploadService } from './upload.service';
import { HttpClientModule } from '@angular/common/http';
import { UploadTextRequest } from '../../model/upload-text-request';
import { UploadBinaryRequest } from '../../model/upload-binary-request';


import { MessageType } from '../../model/message-type';
import { decode } from 'typescript-base64-arraybuffer';
import { ResourceHashMessage } from '../../model/resource-hash-message';
import { switchMap } from '../../../../../node_modules/rxjs/operators';


describe('RemoteUploadService', () => {
  beforeEach(() => {
    TestBed.configureTestingModule({
      imports: [HttpClientModule],
      providers: [RemoteUploadService]
    });
  });

  it('RemoteUploadService instance should be created',
    inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();
    }));


  it('#uploadText should throw error if upload text request payload is null',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      expect(() => service.uploadText(null).subscribe()).toThrowError('The request payload could not be null');
    })));

  it('#uploadText should throw error if upload text data field \'text\' is empty',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const payload: UploadTextRequest = {
        contentType: 'text/plain',
        encoding: 'utf-8',
        name: 'test',
        keywords: '',
        text: 'Proximax P2P Storage',
        metadata: '{\'author\':\'ProximaX\'}'
      }

      expect(() => service.uploadText(payload).subscribe()).toThrowError('The request payload \'text\' field is required');

    })));

  it('#uploadText should throw error if upload text data field \'text\' is null',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const metadata = {
        'author': 'Proximax'
      };


      const payload: UploadTextRequest = {
        contentType: 'text/plain',
        encoding: 'utf-8',
        name: 'test',
        keywords: '',
        text: '',
        metadata: JSON.stringify(metadata)
      }
      expect(() => service.uploadText(payload).subscribe()).toThrowError('The request payload \'text\' field is required');

    })));

  it('#uploadText should throw error if upload metadata field \'text\' is not valid JSON format',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const metadata = {
        'author': 'Proximax'
      };


      const payload: UploadTextRequest = {
        contentType: 'text/plain',
        encoding: 'utf-8',
        name: 'test',
        keywords: '',
        text: 'Proximax P2P Storage',
        metadata: 'test'
      }
      expect(() => service.uploadText(payload).subscribe()).toThrowError('The request payload \'metadata\' field must be a valid JSON');

    })));

  it('#uploadText should return IPFS datahash',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const metadata = {
        'author': 'Proximax'
      };


      const payload: UploadTextRequest = {
        contentType: 'text/plain',
        encoding: 'utf-8',
        name: 'test',
        keywords: '',
        text: 'Proximax P2P Storage',
        metadata: JSON.stringify(metadata)
      }

      service.uploadText(payload).subscribe((response) => {
        // console.log(response);
        const rhm: ResourceHashMessage = response;
        // console.log(rhm.hash());
        // IPFS datahash start with Qm
        expect(rhm.hash().startsWith('Qm')).toBe(true);

        // expect(dataHash.startsWith('Qm')).toBe(true);
      });
    })));

  it('#uploadBytesBinary should return IPFS datahash',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const metadata = {
        'author': 'Proximax'
      };

      // tslint:disable-next-line:max-line-length
      const base64Data = '';

      // need to convert the base64 string to byte array
      const byteArray = [];
      for (let i = 0; i < base64Data.length; i++) {
        byteArray.push(base64Data.charCodeAt(i));
      }

      const payload: UploadBinaryRequest = {
        contentType: 'image/jpeg',
        data: byteArray,
        keywords: '',
        metadata: JSON.stringify(metadata),
        name: 'test'
      }

      service.uploadBinary(payload).subscribe((response) => {
        const rhm: ResourceHashMessage = response;

        // IPFS datahash start with Qm
        console.log(rhm.hash());
        expect(rhm.hash().startsWith('Qm')).toBe(true);
      });
    })));


  it('#uploadSignAnnounce should return AnnounceDataSignature',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const metadata = {
        'author': 'Proximax'
      };

      // tslint:disable-next-line:max-line-length
      const base64Data = '';

      // need to convert the base64 string to byte array
      const byteArray = [];
      for (let i = 0; i < base64Data.length; i++) {
        byteArray.push(base64Data.charCodeAt(i));
      }

      const pubKey = '3be53ccd491d878d7d2a8696ab0a1662ea9644388b81249fccdc921305336190';
      const pvKey = 'e62cccf54864884575a033481cabd4851429203a84849f5fc688fb651efd7f00';
      const messageType = MessageType.PLAIN;

      // const payload = new UploadBinaryRequest('test', byteArray, 'test', JSON.stringify(metadata), 'image/jpeg');

      const payload: UploadBinaryRequest = {
        contentType: 'image/jpeg',
        data: byteArray,
        keywords: '',
        metadata: JSON.stringify(metadata),
        name: 'test'
      }

      service.uploadSignAnnounce(pvKey, pubKey, messageType, payload).subscribe((res) => {

        console.log(res);
        const dataSignature = res.body;
        expect(dataSignature.signature.length > 0).toBe(true);
        expect(dataSignature.data.length > 0).toBe(true);
      });
    })));

  it('#uploadGenerateSign should return AnnounceDataSignature',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();

      const metadata = {
        'author': 'Proximax'
      };

      // tslint:disable-next-line:max-line-length
      const base64Data = '';

      // need to convert the base64 string to blob
      // let data = decode(base64Data);
      const byteCharacters = atob(base64Data);
      const byteArrays = [];
      const sliceSize = 512;
      for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
        const slice = byteCharacters.slice(offset, offset + sliceSize);

        const byteNumbers = new Array(slice.length);
        for (let i = 0; i < slice.length; i++) {
          byteNumbers[i] = slice.charCodeAt(i);
        }

        const byteArray = new Uint8Array(byteNumbers);

        byteArrays.push(byteArray);
      }


      const file = new Blob(byteArrays, { type: 'image/jpeg' });
      // let blob = new Blob(["Hello, world!"], {type: "text/plain;charset=utf-8"});
      const pubKey = '3be53ccd491d878d7d2a8696ab0a1662ea9644388b81249fccdc921305336190';
      const pvKey = 'e62cccf54864884575a033481cabd4851429203a84849f5fc688fb651efd7f00';
      const messageType = MessageType.PLAIN;


      service.uploadGenerateSign(pvKey, pubKey, messageType, file, null, null).subscribe((res) => {

        console.log(res);
        const dataSignature = res.body;
        expect(dataSignature.signature.length > 0).toBe(true);
        expect(dataSignature.data.length > 0).toBe(true);
      });
    })));


  it('#uploadClean should return the generic message',
    async(inject([RemoteUploadService], (service: RemoteUploadService) => {
      expect(service).toBeTruthy();


      // upload text to IPFS for testing
      const metadata = {
        'author': 'Proximax'
      };

     // const payload = new UploadTextRequest('test', 'This is a test 3', 'test', JSON.stringify(metadata), 'text/plain', 'utf-8');


      const payload: UploadTextRequest = {
        contentType: 'text/plain',
        encoding: 'utf-8',
        name: 'test',
        keywords: '',
        text: 'Proximax P2P Storage',
        metadata: JSON.stringify(metadata)
      }

      service.uploadText(payload).pipe(
        switchMap((data) => {
          const rhm: ResourceHashMessage = data;
          // console.log(rhm);
          return service.uploadCleanup(rhm.hash());
        })
      ).subscribe(msg => {
        // console.log(msg);
        expect(msg.status).toBe(202);
      });
    })));
});
